import React from 'react';
import { View, Text, TouchableOpacity, TextInput, StyleSheet } from 'react-native';

const Header = ({ text }) => {
  const handleMenuBtnClick = () => {
    // Lógica para manejar el clic en el botón de menú
  };

  const handleCancelBtnClick = () => {
    // Lógica para manejar el clic en el botón de cancelar
  };

  const handleSearchBtnClick = () => {
    // Lógica para manejar el clic en el botón de búsqueda
  };

  return (
    <View style={styles.container}>
      <View style={styles.menuIcon}>
        {/* Aquí puedes agregar el icono de menú */}
      </View>
      <Text style={styles.logo}>CodingNepal</Text>
      <View style={styles.navItems}>
        {/* Aquí puedes agregar los elementos de navegación */}
      </View>
      <View style={styles.searchIcon}>
        {/* Aquí puedes agregar el icono de búsqueda */}
      </View>
      <View style={styles.cancelIcon}>
        {/* Aquí puedes agregar el icono de cancelar */}
      </View>
      <View style={styles.form}>
        <TextInput
          style={styles.searchData}
          placeholder="Search"
          // Aquí puedes manejar el estado del input de búsqueda
        />
        <TouchableOpacity style={styles.searchButton}>
          {/* Aquí puedes agregar el icono del botón de búsqueda */}
        </TouchableOpacity>
      </View>
      <View style={styles.content}>
        <Text style={styles.header}>Potential new Nav bar</Text>
        <Text style={styles.text}>using HTML CSS & JavaScript</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f2f2f2',
  },
  nav: {
    backgroundColor: '#171c24',
    flexDirection: 'row',
