    borderWidth: 0,
    fontWeight: '500',
    backgroundColor: 'transparent',
  },
  searchButton: {
    padding: 0,
    paddingHorizontal: 15,
    color: '#fff',
    fontSize: 17,
    backgroundColor: '#ff3d00',
    borderWidth: 0,
    borderRadius: 2,
    cursor: 'pointer',
  },
  searchButtonHover: {
    backgroundColor: '#e63600',
